import React, { useContext } from "react";
import { GenericDatesProvider,GenericDates } from './GenericsDate';
import { Line } from 'react-chartjs-2';



import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);



const { dados, atualizarDados } = useContext(GenericDates)

const dadosDia = ['January', 'February', 'March', 'April','Maio'] 

const dadosFatu = [65, 59, 80, 81,19]

const data = {
  labels:dadosDia,
  datasets: [
    // {
    //   label: 'Sales',
    //   data: dadosFatu ,
    //   borderColor: 'rgba(75, 192, 192, 1)',
    //   backgroundColor: 'rgba(75, 192, 192, 0.2)',
    //   borderWidth: 2,
    // },

    {
      label: 'terrenos',
      data: [20, 18, 34, 41, 27, 34],
      borderColor: 'rgba(255, 92, 120, 1)',
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      tension: 0.4, // Linha suavizada
      fill: true,
    },  
    {
      label: 'lojasP',
      data: [18, 28, 24, 41, 27, 44],
      borderColor: 'rgb(118, 22, 43)',
      backgroundColor: 'rgba(255, 99, 132, 0.2)',
      tension: 0.4, // Linha suavizada
      fill: true,
    },
    {
      label: 'lojasM',
      data: [5, 15, 13, 25, 45, 27],
      borderColor: 'rgb(34, 60, 77)',
      backgroundColor: 'rgba(54, 162, 235, 0.2)',
      tension: 0.4,
      fill: true,
    },
    {
      label: 'lojasG',
      data: [2, 12, 12, 32, 42, 52],
      borderColor: 'rgba(75, 192, 192, 1)',
      backgroundColor: 'rgba(75, 192, 192, 0.2)',
      tension: 0.4,
      fill: true,
    }

  ],

};

      const config = {
        type: 'line',
        data: data,
        options: {
          responsive: true,
          plugins: {
            legend: {
              position: 'top',
            },
          },
          scales: {
            x: {
              stacked: true,
            },
            y: {
              stacked: true,
              beginAtZero: true,
            },
          },
        },
      };

function App() {
  return (
  <GenericDatesProvider>
    <div style={{ width: '600px', margin: '50px auto' }}>
      <h2>Sales Data</h2>
      <Line data={data} />
    </div>
  </GenericDatesProvider>

  );
}

export default App;
